package io.loli.box.service;

import io.loli.box.service.impl.FileSystemStorageService;

import java.io.BufferedInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;

/**
 * All storage service should implements this interface
 * 
 * @author choco
 *
 */
public interface StorageService {
    /**
     * Upload an image file
     * 
     * @param file file to upload. After uploaded, file name will be changed to
     *            nano time.
     * @return A url generated by storage service
     * @throws IOException Will be throwed while IO error occurred
     */
    default URL upload(File file) throws IOException {
        return upload(new BufferedInputStream(new FileInputStream(file)), file.getName());
    }

    /**
     * Upload an InputStream with file name
     * 
     * @param is file to upload. After uploaded, file name will be changed to
     *            nano time.
     * @param origin name of this file
     * @return A url generated by storage service
     * @throws IOException Will be throwed while IO error occurred
     */
    URL upload(InputStream is, String filename) throws IOException;

    public static StorageService getDefaultInstance() {
        return new FileSystemStorageService();
    }
}
